import React, { useEffect, useState } from 'react';

const Fence = (data) => {
  const [fenceHeight, setFenceHeight] = useState(props?.fenceHeight);
  const [fenceLength, setFenceLength] = useState(props?.fenceLength);
  const [numPosts, setNumPosts] = useState(props?.numPosts);
  const [fencePosition, setFencePosition] = useState(props?.fencePosition);

  useEffect(() => {
    setFenceHeight(props?.fenceHeight);
    setFenceLength(props?.fenceLength);
    setNumPosts(props?.numPosts);
    setFencePosition(props?.fencePosition);
    console.log(props.props);
  }, [props?.fenceHeight, props?.fenceLength, props?.numPosts, props?.fencePosition]);

    // const fenceHeight = 5;
    // const fenceLength = 100;
    // const numPosts = 22;

    const postDistance = fenceLength / (numPosts - 1);

    const posts = Array.from({ length: numPosts }).map((_, i) => (
        <mesh key={i} position={[i * postDistance - fenceLength / 2, fenceHeight / 2, 0]}>
        <boxGeometry args={[0.1, fenceHeight, 0.1]} />
        <meshStandardMaterial color="brown" />
        </mesh>
    ));

    const bars = Array.from({ length: numPosts - 1 }).map((_, i) => (
        <mesh key={i} position={[(i + 0.5) * postDistance - fenceLength / 2, fenceHeight - 0.5, 0]}>
        <boxGeometry args={[postDistance, 0.05, 0.05]} />
        <meshStandardMaterial color="brown" />
        </mesh>
    ));

    return (
        <group position={fencePosition}>
        {posts}
        {bars}
        </group>
    );
};

export default Fence;
